import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import SignatureCanvas from 'react-signature-canvas';
import axios from 'axios';

export default function VisitorForm() {
  const [name, setName] = useState('');
  const [reason, setReason] = useState('');
  const [photo, setPhoto] = useState(null);
  const webcamRef = useRef(null);
  const sigCanvas = useRef({});

  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setPhoto(imageSrc);
  };

  const clearSignature = () => sigCanvas.current.clear();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const signature = sigCanvas.current.toDataURL('image/png');

    const blobSignature = await fetch(signature).then(res => res.blob());
    const blobPhoto = await fetch(photo).then(res => res.blob());

    const formData = new FormData();
    formData.append('name', name);
    formData.append('reason', reason);
    formData.append('photo', new File([blobPhoto], 'photo.jpg'));
    formData.append('signature', new File([blobSignature], 'signature.png'));

    try {
      const res = await axios.post('http://localhost:5000/api/visitors', formData);
      alert('Visitor Registered Successfully');
      setName('');
      setReason('');
      setPhoto(null);
      clearSignature();
    } catch (err) {
      console.error(err);
      alert('Error registering visitor');
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex justify-center items-center p-6">
      <form onSubmit={handleSubmit} className="bg-gray-800 p-8 rounded-2xl shadow-lg w-full max-w-md space-y-6">
        <h2 className="text-2xl font-semibold text-center">Visitor Check-In</h2>

        <input
          type="text"
          placeholder="Full Name"
          className="w-full p-2 rounded bg-gray-700 text-white"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <input
          type="text"
          placeholder="Reason for Visit"
          className="w-full p-2 rounded bg-gray-700 text-white"
          value={reason}
          onChange={(e) => setReason(e.target.value)}
          required
        />

        <div className="flex flex-col items-center space-y-2">
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            className="rounded-xl"
            width={300}
          />
          <button type="button" onClick={capturePhoto} className="bg-blue-600 px-4 py-1 rounded">
            Capture Photo
          </button>
          {photo && <img src={photo} alt="Captured" className="mt-2 rounded-xl w-40" />}
        </div>

        <div className="border border-gray-500 rounded">
          <SignatureCanvas
            penColor="white"
            canvasProps={{ width: 300, height: 100, className: 'sigCanvas bg-gray-700 rounded' }}
            ref={sigCanvas}
          />
          <button type="button" onClick={clearSignature} className="text-sm text-red-400 px-2 mt-1">
            Clear Signature
          </button>
        </div>

        <button
          type="submit"
          className="w-full bg-green-600 hover:bg-green-700 py-2 rounded text-white font-semibold"
        >
          Submit
        </button>
      </form>
    </div>
  );
}
