import React, { useState, useRef } from 'react';
import { Camera, User, FileText, PenTool, CheckCircle, X, RotateCcw } from 'lucide-react';

export default function VisitorForm() {
  const [name, setName] = useState('');
  const [reason, setReason] = useState('');
  const [photo, setPhoto] = useState(null);
  const [showWebcam, setShowWebcam] = useState(false);
  const [signature, setSignature] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  // Mock webcam capture (since we can't use real webcam in this environment)
  const capturePhoto = () => {
    // In real implementation, this would capture from webcam
    const mockPhoto = 'data:image/svg+xml;base64,' + btoa(`
      <svg width="200" height="150" xmlns="http://www.w3.org/2000/svg">
        <rect width="200" height="150" fill="#4a5568"/>
        <circle cx="100" cy="60" r="25" fill="#a0aec0"/>
        <rect x="80" y="90" width="40" height="30" fill="#a0aec0"/>
        <text x="100" y="130" text-anchor="middle" fill="#e2e8f0" font-size="12">Photo Captured</text>
      </svg>
    `);
    setPhoto(mockPhoto);
    setShowWebcam(false);
  };

  const startDrawing = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    const ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(x, y);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    const ctx = canvas.getContext('2d');
    ctx.lineWidth = 2;
    ctx.lineCap = 'round';
    ctx.strokeStyle = '#ffffff';
    ctx.lineTo(x, y);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(x, y);
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    const canvas = canvasRef.current;
    setSignature(canvas.toDataURL());
  };

  const clearSignature = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setSignature('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Mock submission
    alert('Visitor Registered Successfully! (This is a demo)');
    setName('');
    setReason('');
    setPhoto(null);
    clearSignature();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 flex items-center justify-center p-4">
      <div className="absolute inset-0 bg-black bg-opacity-20"></div>
      
      <div className="relative z-10 w-full max-w-4xl">
        <div className="bg-white bg-opacity-10 backdrop-blur-lg p-8 rounded-3xl shadow-2xl border border-white border-opacity-20">
          
          {/* Header */}
          <div className="text-center mb-10">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full mb-4">
              <User className="w-8 h-8 text-white" />
            </div>
            <h2 className="text-4xl font-bold text-white mb-2">Visitor Registration</h2>
            <p className="text-gray-300">Please fill in your details below</p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            
            {/* Left Column */}
            <div className="space-y-6">
              
              {/* Name Field */}
              <div className="group">
                <label className="flex items-center gap-3 text-white font-medium mb-3">
                  <User className="w-5 h-5 text-blue-400" />
                  Full Name
                </label>
                <input
                  type="text"
                  placeholder="Enter your full name"
                  className="w-full p-4 rounded-xl bg-white bg-opacity-10 text-white placeholder-gray-300 border border-white border-opacity-20 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300 backdrop-blur-sm"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>

              {/* Reason Field */}
              <div className="group">
                <label className="flex items-center gap-3 text-white font-medium mb-3">
                  <FileText className="w-5 h-5 text-green-400" />
                  Purpose of Visit
                </label>
                <textarea
                  placeholder="Please describe the reason for your visit"
                  className="w-full p-4 rounded-xl bg-white bg-opacity-10 text-white placeholder-gray-300 border border-white border-opacity-20 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all duration-300 backdrop-blur-sm resize-none"
                  rows="4"
                  value={reason}
                  onChange={(e) => setReason(e.target.value)}
                  required
                />
              </div>
            </div>

            {/* Right Column */}
            <div className="space-y-6">
              
              {/* Photo Section */}
              <div className="group">
                <label className="flex items-center gap-3 text-white font-medium mb-3">
                  <Camera className="w-5 h-5 text-purple-400" />
                  Photo Capture
                </label>
                <div className="bg-white bg-opacity-10 backdrop-blur-sm rounded-xl border border-white border-opacity-20 p-6">
                  {!photo ? (
                    !showWebcam ? (
                      <div className="text-center">
                        <button
                          type="button"
                          onClick={() => setShowWebcam(true)}
                          className="inline-flex items-center gap-3 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 px-6 py-3 rounded-xl text-white font-medium transition-all duration-300 transform hover:scale-105 shadow-lg"
                        >
                          <Camera className="w-5 h-5" />
                          Take Photo
                        </button>
                        <p className="text-gray-300 text-sm mt-2">Click to capture your photo</p>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        <div className="bg-gray-600 rounded-xl w-full h-48 flex items-center justify-center">
                          <Camera className="w-12 h-12 text-gray-400" />
                        </div>
                        <div className="flex gap-3">
                          <button
                            type="button"
                            onClick={capturePhoto}
                            className="flex-1 bg-green-600 hover:bg-green-700 px-4 py-2 rounded-lg text-white font-medium transition-colors"
                          >
                            Capture
                          </button>
                          <button
                            type="button"
                            onClick={() => setShowWebcam(false)}
                            className="px-4 py-2 bg-gray-600 hover:bg-gray-700 rounded-lg text-white transition-colors"
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    )
                  ) : (
                    <div className="text-center space-y-3">
                      <img src={photo} alt="Captured" className="rounded-xl mx-auto max-w-full h-32 object-cover border-2 border-white border-opacity-20" />
                      <button
                        type="button"
                        onClick={() => setPhoto(null)}
                        className="inline-flex items-center gap-2 text-red-400 hover:text-red-300 text-sm transition-colors"
                      >
                        <X className="w-4 h-4" />
                        Retake Photo
                      </button>
                    </div>
                  )}
                </div>
              </div>

              {/* Signature Section */}
              <div className="group">
                <label className="flex items-center gap-3 text-white font-medium mb-3">
                  <PenTool className="w-5 h-5 text-yellow-400" />
                  Digital Signature
                </label>
                <div className="bg-white bg-opacity-10 backdrop-blur-sm rounded-xl border border-white border-opacity-20 p-4">
                  <canvas
                    ref={canvasRef}
                    width={300}
                    height={120}
                    className="w-full h-24 bg-white bg-opacity-5 rounded-lg border border-white border-opacity-20 cursor-crosshair"
                    onMouseDown={startDrawing}
                    onMouseMove={draw}
                    onMouseUp={stopDrawing}
                    onMouseLeave={stopDrawing}
                  />
                  <div className="flex justify-between items-center mt-3">
                    <p className="text-gray-300 text-sm">Sign above</p>
                    <button
                      type="button"
                      onClick={clearSignature}
                      className="inline-flex items-center gap-2 text-red-400 hover:text-red-300 text-sm transition-colors"
                    >
                      <RotateCcw className="w-4 h-4" />
                      Clear
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Submit Button */}
          <div className="mt-10">
            <button
              onClick={(e) => {
                e.preventDefault();
                handleSubmit(e);
              }}
              className="w-full bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 py-4 rounded-xl text-white font-semibold text-lg transition-all duration-300 transform hover:scale-[1.02] shadow-lg flex items-center justify-center gap-3"
            >
              <CheckCircle className="w-6 h-6" />
              Register Visitor
            </button>
          </div>

          {/* Footer */}
          <div className="text-center mt-6">
            <p className="text-gray-400 text-sm">All information is securely stored and processed</p>
          </div>
        </div>
      </div>
    </div>
  );
}
