import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';

// Custom SVG Icons
const UserIcon = ({ style }) => (
  <svg style={style} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
  </svg>
);
 const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setPhoto(imageSrc);
    setShowWebcam(false);
  };
const CameraIcon = ({ style }) => (
  <svg style={style} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M4 5a2 2 0 00-2 2v8a2 2 0 002 2h12a2 2 0 002-2V7a2 2 0 00-2-2h-1.586a1 1 0 01-.707-.293l-1.121-1.121A2 2 0 0011.172 3H8.828a2 2 0 00-1.414.586L6.293 4.707A1 1 0 015.586 5H4zm6 9a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd" />
  </svg>
);

const FileIcon = ({ style }) => (
  <svg style={style} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M4 4a2 2 0 012-2h4.586A2 2 0 0112 2.586L15.414 6A2 2 0 0116 7.414V16a2 2 0 01-2 2H6a2 2 0 01-2-2V4zm2 6a1 1 0 011-1h6a1 1 0 110 2H7a1 1 0 01-1-1zm1 3a1 1 0 100 2h6a1 1 0 100-2H7z" clipRule="evenodd" />
  </svg>
);

const PenIcon = ({ style }) => (
  <svg style={style} fill="currentColor" viewBox="0 0 20 20">
    <path d="M13.586 3.586a2 2 0 112.828 2.828l-.793.793-2.828-2.828.793-.793zM11.379 5.793L3 14.172V17h2.828l8.38-8.379-2.83-2.828z" />
  </svg>
);

const CheckIcon = ({ style }) => (
  <svg style={style} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
  </svg>
);

const CloseIcon = ({ style }) => (
  <svg style={style} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
  </svg>
);

const RefreshIcon = ({ style }) => (
  <svg style={style} fill="currentColor" viewBox="0 0 20 20">
    <path fillRule="evenodd" d="M4 2a1 1 0 011 1v2.101a7.002 7.002 0 0111.601 2.566 1 1 0 11-1.885.666A5.002 5.002 0 005.999 7H9a1 1 0 010 2H4a1 1 0 01-1-1V3a1 1 0 011-1zm.008 9.057a1 1 0 011.276.61A5.002 5.002 0 0014.001 13H11a1 1 0 110-2h5a1 1 0 011 1v5a1 1 0 11-2 0v-2.101a7.002 7.002 0 01-11.601-2.566 1 1 0 01.61-1.276z" clipRule="evenodd" />
  </svg>
);

export default function VisitorForm() {
  const [name, setName] = useState('');
  const [reason, setReason] = useState('');
  const [photo, setPhoto] = useState(null);
  const [showWebcam, setShowWebcam] = useState(false);
  const [signature, setSignature] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  // Mock webcam capture (since we can't use real webcam in this environment)
  const capturePhoto = () => {
    // In real implementation, this would capture from webcam
    const mockPhoto = 'data:image/svg+xml;base64,' + btoa(`
      <svg width="200" height="150" xmlns="http://www.w3.org/2000/svg">
        <rect width="200" height="150" fill="#4a5568"/>
        <circle cx="100" cy="60" r="25" fill="#a0aec0"/>
        <rect x="80" y="90" width="40" height="30" fill="#a0aec0"/>
        <text x="100" y="130" text-anchor="middle" fill="#e2e8f0" font-size="12">Photo Captured</text>
      </svg>
    `);
    setPhoto(mockPhoto);
    setShowWebcam(false);
  };

  const startDrawing = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    const ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(x, y);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    const ctx = canvas.getContext('2d');
    ctx.lineWidth = 2;
    ctx.lineCap = 'round';
    ctx.strokeStyle = '#ffffff';
    ctx.lineTo(x, y);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(x, y);
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    const canvas = canvasRef.current;
    setSignature(canvas.toDataURL());
  };

  const clearSignature = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setSignature('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/visitors`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name,
          reason,
          photo,
          signature
        })
      });
  
      const data = await response.json();
  
      if (response.ok) {
        alert(data.message || 'Visitor Registered Successfully!');
        setName('');
        setReason('');
        setPhoto(null);
        clearSignature();
      } else {
        alert(data.message || 'Something went wrong.');
        console.error(data.error);
      }
    } catch (error) {
      alert('Server error. Please try again later.');
      console.error('Error submitting form:', error);
    }
  };
  

  const containerStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #1e1b4b 0%, #7c3aed 50%, #ec4899 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '2rem 1rem',
    position: 'relative'
  };

  const overlayStyle = {
    position: 'absolute',
    inset: '0',
    backgroundColor: 'rgba(0, 0, 0, 0.2)'
  };

  const formStyle = {
    position: 'relative',
    zIndex: '10',
    width: '100%',
    maxWidth: '1000px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(10px)',
    padding: '2rem',
    borderRadius: '24px',
    boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
    border: '1px solid rgba(255, 255, 255, 0.2)'
  };

  const headerStyle = {
    textAlign: 'center',
    marginBottom: '2.5rem'
  };

  const iconContainerStyle = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '4rem',
    height: '4rem',
    background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
    borderRadius: '50%',
    marginBottom: '1rem'
  };

  const titleStyle = {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '0.5rem'
  };

  const subtitleStyle = {
    color: '#d1d5db'
  };

  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
    gap: '2rem'
  };

  const fieldStyle = {
    marginBottom: '1.5rem'
  };

  const labelStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
    color: 'white',
    fontWeight: '500',
    marginBottom: '0.75rem'
  };

  const inputStyle = {
    width: '100%',
    padding: '1rem',
    borderRadius: '12px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    color: 'white',
    border: '1px solid rgba(255, 255, 255, 0.2)',
     fontSize: '1rem'
  };

  const textareaStyle = {
    ...inputStyle,
    resize: 'none',
    minHeight: '100px'
  };

  const cardStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(4px)',
    borderRadius: '12px',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    padding: '1.5rem'
  };

  const buttonPrimaryStyle = {
    background: 'linear-gradient(135deg, #8b5cf6, #ec4899)',
    color: 'white',
    fontWeight: '500',
    padding: '0.75rem 1.5rem',
    borderRadius: '12px',
    border: 'none',
    cursor: 'pointer',
    display: 'inline-flex',
    alignItems: 'center',
    gap: '0.75rem',
    transition: 'all 0.3s ease',
    fontSize: '1rem'
  };

  const buttonSecondaryStyle = {
    backgroundColor: '#4b5563',
    color: 'white',
    padding: '0.5rem 1rem',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    transition: 'all 0.3s ease'
  };

  const submitButtonStyle = {
    width: '100%',
    background: 'linear-gradient(135deg, #059669, #10b981)',
    color: 'white',
    fontWeight: '600',
    padding: '1rem',
    borderRadius: '12px',
    border: 'none',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '0.75rem',
    fontSize: '1.125rem',
    marginTop: '2rem',
    transition: 'all 0.3s ease'
  };

  const canvasStyle = {
    width: '100%',
    height: '80px',
    backgroundColor: 'rgba(255, 255, 255, 0.05)',
    borderRadius: '8px',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    cursor: 'crosshair'
  };

  return (
    <div style={containerStyle}>
      <div style={overlayStyle}></div>
      
      <div style={formStyle}>
        {/* Header */}
        <div style={headerStyle}>
          <div style={iconContainerStyle}>
            <UserIcon style={{ width: '2rem', height: '2rem', color: 'white' }} />
          </div>
          <h2 style={titleStyle}>Visitor Registration</h2>
          <p style={subtitleStyle}>Please fill in your details below</p>
        </div>

        <div style={gridStyle}>
          {/* Left Column */}
          <div>
            {/* Name Field */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <UserIcon style={{ color: '#60a5fa', width: '1.25rem', height: '1.25rem' }} />
                Full Name
              </label>
              <input
                type="text"
                placeholder="Enter your full name"
                style={inputStyle}
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            {/* Reason Field */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <FileIcon style={{ color: '#4ade80', width: '1.25rem', height: '1.25rem' }} />
                Purpose of Visit
              </label>
              <textarea
                placeholder="Please describe the reason for your visit"
                style={textareaStyle}
                value={reason}
                onChange={(e) => setReason(e.target.value)}
                required
              />
            </div>
          </div>

          {/* Right Column */}
          <div>
            {/* Photo Section */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <CameraIcon style={{ color: '#a78bfa', width: '1.25rem', height: '1.25rem' }} />
                Photo Capture
              </label>
              <div style={cardStyle}>
                {!photo ? (
                  !showWebcam ? (
                    <div style={{ textAlign: 'center' }}>
                      <button
                        type="button"
                        onClick={() => setShowWebcam(true)}
                        style={buttonPrimaryStyle}
                      >
                        <CameraIcon style={{}} />
                        Take Photo
                      </button>
                      <p style={{ color: '#d1d5db', fontSize: '0.875rem', marginTop: '0.5rem' }}>
                        Click to capture your photo
                      </p>
                    </div>
                  ) : (
                    <div style={{ textAlign: 'center' }}>
                      <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        width="100%"
                        videoConstraints={{ facingMode: 'user' }}
                        style={{
                          borderRadius: '12px',
                          maxWidth: '100%',
                          height: '200px',
                          objectFit: 'cover',
                          marginBottom: '1rem'
                        }}
                      />
                      <div style={{ display: 'flex', gap: '0.75rem', justifyContent: 'center' }}>
                        <button
                          type="button"
                          onClick={capturePhoto}
                          style={{
                            backgroundColor: '#059669',
                            color: 'white',
                            padding: '0.5rem 1rem',
                            borderRadius: '8px',
                            border: 'none',
                            cursor: 'pointer'
                          }}
                        >
                          Capture
                        </button>
                        <button
                          type="button"
                          onClick={() => setShowWebcam(false)}
                          style={buttonSecondaryStyle}
                        >
                          Cancel
                        </button>
                      </div>
                    </div>
                  )
                ) : (
                  <div style={{ textAlign: 'center' }}>
                    <img
                      src={photo}
                      alt="Captured"
                      style={{
                        borderRadius: '12px',
                        maxWidth: '100%',
                        height: '150px',
                        objectFit: 'cover',
                        border: '2px solid rgba(255, 255, 255, 0.2)',
                        marginBottom: '0.75rem'
                      }}
                    />
                    <button
                      type="button"
                      onClick={() => setPhoto(null)}
                      style={{
                        color: '#f87171',
                        backgroundColor: 'transparent',
                        border: 'none',
                        cursor: 'pointer',
                        fontSize: '0.875rem',
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.5rem',
                        margin: '0 auto'
                      }}
                    >
                      <CloseIcon style={{ width: '1rem', height: '1rem' }} />
                      Retake Photo
                    </button>
                  </div>
                )}
              </div>
            </div>


            {/* Signature Section */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <PenIcon style={{ color: '#fbbf24', width: '1.25rem', height: '1.25rem' }} />
                Digital Signature
              </label>
              <div style={cardStyle}>
                <canvas
                  ref={canvasRef}
                  width={300}
                  height={80}
                  style={canvasStyle}
                  onMouseDown={startDrawing}
                  onMouseMove={draw}
                  onMouseUp={stopDrawing}
                  onMouseLeave={stopDrawing}
                />
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center', 
                  marginTop: '0.75rem' 
                }}>
                  <p style={{ color: '#d1d5db', fontSize: '0.875rem' }}>Sign above</p>
                  <button
                    type="button"
                    onClick={clearSignature}
                    style={{
                      color: '#f87171',
                      backgroundColor: 'transparent',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '0.875rem',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem'
                    }}
                  >
                    <RefreshIcon style={{ width: '1rem', height: '1rem' }} />
                    Clear
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <button
          onClick={(e) => {
            e.preventDefault();
            handleSubmit(e);
          }}
          style={submitButtonStyle}
        >
          <CheckIcon style={{ width: '1.5rem', height: '1.5rem' }} />
          Register Visitor
        </button>

        {/* Footer */}
        <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
          <p style={{ color: '#9ca3af', fontSize: '0.875rem' }}>
            All information is securely stored and processed
          </p>
        </div>
      </div>
    </div>
  );
}
