import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import SignatureCanvas from 'react-signature-canvas';
import axios from 'axios';
import { FaCamera } from 'react-icons/fa';

export default function VisitorForm() {
  const [name, setName] = useState('');
  const [reason, setReason] = useState('');
  const [photo, setPhoto] = useState(null);
  const [showWebcam, setShowWebcam] = useState(false);
  const webcamRef = useRef(null);
  const sigCanvas = useRef({});

  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setPhoto(imageSrc);
    setShowWebcam(false);
  };

  const clearSignature = () => sigCanvas.current.clear();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const signature = sigCanvas.current.toDataURL('image/png');
    const blobSignature = await fetch(signature).then(res => res.blob());
    const blobPhoto = await fetch(photo).then(res => res.blob());

    const formData = new FormData();
    formData.append('name', name);
    formData.append('reason', reason);
    formData.append('photo', new File([blobPhoto], 'photo.jpg'));
    formData.append('signature', new File([blobSignature], 'signature.png'));

    try {
      await axios.post('http://localhost:5000/api/visitors', formData);
      alert('Visitor Registered Successfully');
      setName('');
      setReason('');
      setPhoto(null);
      clearSignature();
    } catch (err) {
      console.error(err);
      alert('Error registering visitor');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-gray-800 p-8 rounded-2xl shadow-lg w-full max-w-md space-y-6">
      <h2 className="text-3xl font-bold text-center mb-6">Visitor Registration</h2>

      <div className="space-y-1">
        <label className="block text-sm text-gray-300">Full Name</label>
        <input
          type="text"
          placeholder="Enter full name"
          className="w-full p-3 rounded bg-gray-700 text-white"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>

      <div className="space-y-1">
        <label className="block text-sm text-gray-300">Reason for Visit</label>
        <input
          type="text"
          placeholder="Enter reason"
          className="w-full p-3 rounded bg-gray-700 text-white"
          value={reason}
          onChange={(e) => setReason(e.target.value)}
          required
        />
      </div>

      <div className="space-y-2">
        <label className="block text-sm text-gray-300">Capture Photo</label>
        {!photo ? (
          <div className="flex flex-col items-center">
            {!showWebcam ? (
              <button
                type="button"
                onClick={() => setShowWebcam(true)}
                className="bg-blue-600 hover:bg-blue-700 p-3 rounded-full"
              >
                <FaCamera size={24} />
              </button>
            ) : (
              <>
                <Webcam
                  audio={false}
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  className="rounded-xl mb-2"
                  width={300}
                />
                <button type="button" onClick={capturePhoto} className="bg-green-500 px-4 py-1 rounded">
                  Capture Photo
                </button>
              </>
            )}
          </div>
        ) : (
          <img src={photo} alt="Captured" className="captured" />
        )}
      </div>

      <div className="space-y-1">
        <label className="block text-sm text-gray-300">Digital Signature</label>
        <SignatureCanvas
          penColor="white"
          canvasProps={{ width: 300, height: 100, className: 'sigCanvas' }}
          ref={sigCanvas}
        />
        <button type="button" onClick={clearSignature} className="text-sm text-red-400 mt-1">
          Clear Signature
        </button>
      </div>

      <button
        type="submit"
        className="w-full bg-green-600 hover:bg-green-700 py-3 rounded text-white font-semibold mt-4"
      >
        Submit
      </button>
    </form>
  );
}
