import React, { useState, useRef } from 'react';

const VisitorForm = () => {
  const [visitorData, setVisitorData] = useState({
    name: '',
    phone: '',
    whomToMeet: '',
    purpose: '',
    address: '',
    signature: '',
    photo: '',
  });

  const signatureRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setVisitorData({ ...visitorData, [name]: value });
  };

  const handleClearSignature = () => {
    const canvas = signatureRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setVisitorData({ ...visitorData, signature: '' });
  };

  const handleTakePhoto = () => {
    // Dummy placeholder image, can be replaced with real webcam integration
    const dummyPhoto =
      'https://via.placeholder.com/150?text=Visitor+Photo';
    setVisitorData({ ...visitorData, photo: dummyPhoto });
  };

  const handleSaveSignature = () => {
    const canvas = signatureRef.current;
    const signatureData = canvas.toDataURL();
    setVisitorData({ ...visitorData, signature: signatureData });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !visitorData.name ||
      !visitorData.phone ||
      !visitorData.whomToMeet ||
      !visitorData.purpose ||
      !visitorData.address ||
      !visitorData.signature ||
      !visitorData.photo
    ) {
      alert('Please fill all fields and capture photo/signature.');
      return;
    }

    console.log('Submitted Visitor Data:', visitorData);
    alert('Form submitted successfully!');
    // Reset form
    setVisitorData({
      name: '',
      phone: '',
      whomToMeet: '',
      purpose: '',
      address: '',
      signature: '',
      photo: '',
    });
    handleClearSignature();
  };

  return (
    <div className="visitor-form-container" style={styles.container}>
      <h2 style={styles.title}>Visitor Information</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={visitorData.name}
          onChange={handleInputChange}
          style={styles.input}
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone Number"
          value={visitorData.phone}
          onChange={handleInputChange}
          style={styles.input}
        />
        <input
          type="text"
          name="whomToMeet"
          placeholder="Whom to Meet"
          value={visitorData.whomToMeet}
          onChange={handleInputChange}
          style={styles.input}
        />
        <input
          type="text"
          name="purpose"
          placeholder="Purpose"
          value={visitorData.purpose}
          onChange={handleInputChange}
          style={styles.input}
        />
        <textarea
          name="address"
          placeholder="Address"
          value={visitorData.address}
          onChange={handleInputChange}
          style={styles.textarea}
        />

        <div style={styles.signatureSection}>
          <label style={styles.label}>Signature:</label>
          <canvas
            ref={signatureRef}
            width={300}
            height={100}
            style={styles.canvas}
            onMouseDown={(e) => startDrawing(e)}
            onMouseMove={(e) => draw(e)}
            onMouseUp={stopDrawing}
            onMouseLeave={stopDrawing}
          />
          <button type="button" onClick={handleClearSignature} style={styles.clearButton}>
            Clear Signature
          </button>
          <button type="button" onClick={handleSaveSignature} style={styles.saveButton}>
            Save Signature
          </button>
        </div>

        <div style={styles.photoSection}>
          <label style={styles.label}>Photo:</label>
          {visitorData.photo && <img src={visitorData.photo} alt="Visitor" style={styles.photo} />}
          <button type="button" onClick={handleTakePhoto} style={styles.takePhotoButton}>
            Take Photo
          </button>
        </div>

        <button type="submit" style={styles.submitButton}>
          Submit
        </button>
      </form>
    </div>
  );

  // Signature drawing functions
  let isDrawing = false;
  function startDrawing(e) {
    isDrawing = true;
    const ctx = signatureRef.current.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
  }

  function draw(e) {
    if (!isDrawing) return;
    const ctx = signatureRef.current.getContext('2d');
    ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    ctx.stroke();
  }

  function stopDrawing() {
    isDrawing = false;
  }
};

// Styles (preserve your design, modify colors/sizes if needed)
const styles = {
  container: {
    padding: '20px',
    maxWidth: '500px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  title: {
    textAlign: 'center',
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  input: {
    padding: '10px',
    fontSize: '14px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  textarea: {
    padding: '10px',
    fontSize: '14px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    resize: 'vertical',
  },
  label: {
    fontWeight: 'bold',
    marginBottom: '4px',
  },
  signatureSection: {
    marginTop: '10px',
  },
  canvas: {
    border: '1px solid #000',
    display: 'block',
    marginBottom: '8px',
    backgroundColor: '#fff',
  },
  clearButton: {
    padding: '6px 10px',
    marginRight: '10px',
    backgroundColor: '#e74c3c',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  saveButton: {
    padding: '6px 10px',
    backgroundColor: '#3498db',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  photoSection: {
    marginTop: '15px',
  },
  photo: {
    width: '150px',
    height: '150px',
    objectFit: 'cover',
    marginBottom: '8px',
    borderRadius: '8px',
    border: '1px solid #ccc',
  },
  takePhotoButton: {
    padding: '6px 10px',
    backgroundColor: '#27ae60',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  submitButton: {
    padding: '10px',
    marginTop: '15px',
    backgroundColor: '#2c3e50',
    color: 'white',
    fontSize: '16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
  },
};

export default VisitorForm;
