import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import SignatureCanvas from 'react-signature-canvas';
import axios from 'axios';
import { FaCamera } from 'react-icons/fa';

export default function VisitorForm() {
  const [name, setName] = useState('');
  const [reason, setReason] = useState('');
  const [photo, setPhoto] = useState(null);
  const [showWebcam, setShowWebcam] = useState(false);
  const webcamRef = useRef(null);
  const sigCanvas = useRef({});

  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setPhoto(imageSrc);
    setShowWebcam(false);
  };

  const clearSignature = () => sigCanvas.current.clear();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const signature = sigCanvas.current.toDataURL('image/png');
    const blobSignature = await fetch(signature).then(res => res.blob());
    const blobPhoto = await fetch(photo).then(res => res.blob());

    const formData = new FormData();
    formData.append('name', name);
    formData.append('reason', reason);
    formData.append('photo', new File([blobPhoto], 'photo.jpg'));
    formData.append('signature', new File([blobSignature], 'signature.png'));

    try {
      await axios.post('http://localhost:5000/api/visitors', formData);
      alert('Visitor Registered Successfully');
      setName('');
      setReason('');
      setPhoto(null);
      clearSignature();
    } catch (err) {
      console.error(err);
      alert('Error registering visitor');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-800 to-slate-900 flex items-center justify-center p-6">
      <form
        onSubmit={handleSubmit}
        className="bg-slate-800 p-10 rounded-2xl shadow-2xl w-full max-w-2xl border border-slate-700 space-y-8"
      >
        <h2 className="text-4xl font-bold text-center text-white mb-8">Visitor Registration</h2>
  
        <div className="flex items-center gap-6">
          <label className="w-1/4 text-lg text-white font-medium">Full Name</label>
          <input
            type="text"
            placeholder="Enter full name"
            className="flex-1 p-3 rounded-md bg-slate-700 text-white border border-slate-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
  
        <div className="flex items-center gap-6">
          <label className="w-1/4 text-lg text-white font-medium">Reason</label>
          <input
            type="text"
            placeholder="Enter reason for visiting"
            className="flex-1 p-3 rounded-md bg-slate-700 text-white border border-slate-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            required
          />
        </div>
  
        <div className="flex items-center gap-6">
          <label className="w-1/4 text-lg text-white font-medium flex items-center gap-2">
            <FaCamera /> Camera
          </label>
          <div className="flex-1">
            {!photo ? (
              !showWebcam ? (
                <button
                  type="button"
                  onClick={() => setShowWebcam(true)}
                  className="bg-blue-600 hover:bg-blue-700 p-3 rounded-full"
                >
                  <FaCamera size={24} className="text-white" />
                </button>
              ) : (
                <div className="space-y-3">
                  <Webcam
                    audio={false}
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    className="rounded-xl border border-slate-600"
                    width={320}
                  />
                  <button
                    type="button"
                    onClick={capturePhoto}
                    className="bg-green-500 px-4 py-2 rounded text-white hover:bg-green-600"
                  >
                    Capture Photo
                  </button>
                </div>
              )
            ) : (
              <img src={photo} alt="Captured" className="rounded-lg w-40 h-auto border border-slate-600" />
            )}
          </div>
        </div>
  
        <div className="flex items-center gap-6">
          <label className="w-1/4 text-lg text-white font-medium">Signature</label>
          <div className="flex-1">
            <SignatureCanvas
              penColor="white"
              canvasProps={{
                width: 300,
                height: 100,
                className: 'rounded bg-slate-700 border border-slate-500',
              }}
              ref={sigCanvas}
            />
            <button
              type="button"
              onClick={clearSignature}
              className="text-sm text-red-400 mt-2 hover:underline"
            >
              Clear Signature
            </button>
          </div>
        </div>
  
        <button
          type="submit"
          className="w-full bg-green-600 hover:bg-green-700 py-3 rounded-md text-white font-semibold text-lg mt-4"
        >
          Submit
        </button>
      </form>
    </div>
  );
  
}
