import React, { useState, useRef } from 'react';
import { FaCamera, FaUser, FaFileAlt, FaPen, FaCheckCircle, FaTimes, FaRedo } from 'react-icons/fa';

export default function VisitorForm() {
  const [name, setName] = useState('');
  const [reason, setReason] = useState('');
  const [photo, setPhoto] = useState(null);
  const [showWebcam, setShowWebcam] = useState(false);
  const [signature, setSignature] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  // Mock webcam capture (since we can't use real webcam in this environment)
  const capturePhoto = () => {
    // In real implementation, this would capture from webcam
    const mockPhoto = 'data:image/svg+xml;base64,' + btoa(`
      <svg width="200" height="150" xmlns="http://www.w3.org/2000/svg">
        <rect width="200" height="150" fill="#4a5568"/>
        <circle cx="100" cy="60" r="25" fill="#a0aec0"/>
        <rect x="80" y="90" width="40" height="30" fill="#a0aec0"/>
        <text x="100" y="130" text-anchor="middle" fill="#e2e8f0" font-size="12">Photo Captured</text>
      </svg>
    `);
    setPhoto(mockPhoto);
    setShowWebcam(false);
  };

  const startDrawing = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    const ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(x, y);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    const ctx = canvas.getContext('2d');
    ctx.lineWidth = 2;
    ctx.lineCap = 'round';
    ctx.strokeStyle = '#ffffff';
    ctx.lineTo(x, y);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(x, y);
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    const canvas = canvasRef.current;
    setSignature(canvas.toDataURL());
  };

  const clearSignature = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setSignature('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Mock submission
    alert('Visitor Registered Successfully! (This is a demo)');
    setName('');
    setReason('');
    setPhoto(null);
    clearSignature();
  };

  const containerStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #1e1b4b 0%, #7c3aed 50%, #ec4899 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '2rem 1rem',
    position: 'relative'
  };

  const overlayStyle = {
    position: 'absolute',
    inset: '0',
    backgroundColor: 'rgba(0, 0, 0, 0.2)'
  };

  const formStyle = {
    position: 'relative',
    zIndex: '10',
    width: '100%',
    maxWidth: '1000px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(10px)',
    padding: '2rem',
    borderRadius: '24px',
    boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
    border: '1px solid rgba(255, 255, 255, 0.2)'
  };

  const headerStyle = {
    textAlign: 'center',
    marginBottom: '2.5rem'
  };

  const iconContainerStyle = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '4rem',
    height: '4rem',
    background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
    borderRadius: '50%',
    marginBottom: '1rem'
  };

  const titleStyle = {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '0.5rem'
  };

  const subtitleStyle = {
    color: '#d1d5db'
  };

  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
    gap: '2rem'
  };

  const fieldStyle = {
    marginBottom: '1.5rem'
  };

  const labelStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
    color: 'white',
    fontWeight: '500',
    marginBottom: '0.75rem'
  };

  const inputStyle = {
    width: '100%',
    padding: '1rem',
    borderRadius: '12px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    color: 'white',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    backdropFilter: 'blur(4px)',
    fontSize: '1rem'
  };

  const textareaStyle = {
    ...inputStyle,
    resize: 'none',
    minHeight: '100px'
  };

  const cardStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(4px)',
    borderRadius: '12px',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    padding: '1.5rem'
  };

  const buttonPrimaryStyle = {
    background: 'linear-gradient(135deg, #8b5cf6, #ec4899)',
    color: 'white',
    fontWeight: '500',
    padding: '0.75rem 1.5rem',
    borderRadius: '12px',
    border: 'none',
    cursor: 'pointer',
    display: 'inline-flex',
    alignItems: 'center',
    gap: '0.75rem',
    transition: 'all 0.3s ease',
    fontSize: '1rem'
  };

  const buttonSecondaryStyle = {
    backgroundColor: '#4b5563',
    color: 'white',
    padding: '0.5rem 1rem',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    transition: 'all 0.3s ease'
  };

  const submitButtonStyle = {
    width: '100%',
    background: 'linear-gradient(135deg, #059669, #10b981)',
    color: 'white',
    fontWeight: '600',
    padding: '1rem',
    borderRadius: '12px',
    border: 'none',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '0.75rem',
    fontSize: '1.125rem',
    marginTop: '2rem',
    transition: 'all 0.3s ease'
  };

  const canvasStyle = {
    width: '100%',
    height: '80px',
    backgroundColor: 'rgba(255, 255, 255, 0.05)',
    borderRadius: '8px',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    cursor: 'crosshair'
  };

  return (
    <div style={containerStyle}>
      <div style={overlayStyle}></div>
      
      <div style={formStyle}>
        {/* Header */}
        <div style={headerStyle}>
          <div style={iconContainerStyle}>
            <FaUser style={{ width: '2rem', height: '2rem', color: 'white' }} />
          </div>
          <h2 style={titleStyle}>Visitor Registration</h2>
          <p style={subtitleStyle}>Please fill in your details below</p>
        </div>

        <div style={gridStyle}>
          {/* Left Column */}
          <div>
            {/* Name Field */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <FaUser style={{ color: '#60a5fa' }} />
                Full Name
              </label>
              <input
                type="text"
                placeholder="Enter your full name"
                style={inputStyle}
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            {/* Reason Field */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <FaFileAlt style={{ color: '#4ade80' }} />
                Purpose of Visit
              </label>
              <textarea
                placeholder="Please describe the reason for your visit"
                style={textareaStyle}
                value={reason}
                onChange={(e) => setReason(e.target.value)}
                required
              />
            </div>
          </div>

          {/* Right Column */}
          <div>
            {/* Photo Section */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <FaCamera style={{ color: '#a78bfa' }} />
                Photo Capture
              </label>
              <div style={cardStyle}>
                {!photo ? (
                  !showWebcam ? (
                    <div style={{ textAlign: 'center' }}>
                      <button
                        type="button"
                        onClick={() => setShowWebcam(true)}
                        style={buttonPrimaryStyle}
                      >
                        <FaCamera />
                        Take Photo
                      </button>
                      <p style={{ color: '#d1d5db', fontSize: '0.875rem', marginTop: '0.5rem' }}>
                        Click to capture your photo
                      </p>
                    </div>
                  ) : (
                    <div style={{ textAlign: 'center' }}>
                      <div style={{ 
                        backgroundColor: '#4b5563', 
                        borderRadius: '12px', 
                        width: '100%', 
                        height: '200px', 
                        display: 'flex', 
                        alignItems: 'center', 
                        justifyContent: 'center',
                        marginBottom: '1rem'
                      }}>
                        <FaCamera style={{ width: '3rem', height: '3rem', color: '#9ca3af' }} />
                      </div>
                      <div style={{ display: 'flex', gap: '0.75rem' }}>
                        <button
                          type="button"
                          onClick={capturePhoto}
                          style={{
                            ...buttonSecondaryStyle,
                            backgroundColor: '#059669',
                            flex: '1'
                          }}
                        >
                          Capture
                        </button>
                        <button
                          type="button"
                          onClick={() => setShowWebcam(false)}
                          style={buttonSecondaryStyle}
                        >
                          Cancel
                        </button>
                      </div>
                    </div>
                  )
                ) : (
                  <div style={{ textAlign: 'center' }}>
                    <img 
                      src={photo} 
                      alt="Captured" 
                      style={{ 
                        borderRadius: '12px', 
                        maxWidth: '100%', 
                        height: '150px', 
                        objectFit: 'cover',
                        border: '2px solid rgba(255, 255, 255, 0.2)',
                        marginBottom: '0.75rem'
                      }} 
                    />
                    <button
                      type="button"
                      onClick={() => setPhoto(null)}
                      style={{
                        color: '#f87171',
                        backgroundColor: 'transparent',
                        border: 'none',
                        cursor: 'pointer',
                        fontSize: '0.875rem',
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.5rem',
                        margin: '0 auto'
                      }}
                    >
                      <FaTimes />
                      Retake Photo
                    </button>
                  </div>
                )}
              </div>
            </div>

            {/* Signature Section */}
            <div style={fieldStyle}>
              <label style={labelStyle}>
                <FaPen style={{ color: '#fbbf24' }} />
                Digital Signature
              </label>
              <div style={cardStyle}>
                <canvas
                  ref={canvasRef}
                  width={300}
                  height={80}
                  style={canvasStyle}
                  onMouseDown={startDrawing}
                  onMouseMove={draw}
                  onMouseUp={stopDrawing}
                  onMouseLeave={stopDrawing}
                />
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center', 
                  marginTop: '0.75rem' 
                }}>
                  <p style={{ color: '#d1d5db', fontSize: '0.875rem' }}>Sign above</p>
                  <button
                    type="button"
                    onClick={clearSignature}
                    style={{
                      color: '#f87171',
                      backgroundColor: 'transparent',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '0.875rem',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem'
                    }}
                  >
                    <FaRedo />
                    Clear
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <button
          onClick={(e) => {
            e.preventDefault();
            handleSubmit(e);
          }}
          style={submitButtonStyle}
        >
          <FaCheckCircle />
          Register Visitor
        </button>

        {/* Footer */}
        <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
          <p style={{ color: '#9ca3af', fontSize: '0.875rem' }}>
            All information is securely stored and processed
          </p>
        </div>
      </div>
    </div>
  );
}
